import React, {useState} from 'react'
import {Link} from 'react-router-dom'
import axios from 'axios'

function Login() {
    const [user, setUser] = useState({
        email:'', password: ''
    })

    const onChangeInput = e =>{
        const {name, value} = e.target;
        setUser({...user, [name]: value})
    }

    const loginSubmit = async e =>{
        e.preventDefault()
        try {
            await axios.post('/user/login', {...user})

            localStorage.setItem('firstLogin', true)
            window.location.hre
        } catch (err) {
            alert(err.response.data.msg)
        }
    }

    return (
        <div className='login-page'>
            <form onSubmit={loginSubmit} >
                <input type="text" name='Id' required placeholder='User ID' value={user.Id} onChange={onChangeInput}/>

                <input type="password" name='password' required autoComplete="on" placeholder='Password' value={user.password} onChange={onChangeInput} />

                <div className="row">
                    <button type="submit" >Login</button>
                    <Link to="/register">Register</Link>
                </div>
            </form>
        </div>
    )
}

export default Login
